CREATE TABLE `form`.`user` ( `id` INT NOT NULL AUTO_INCREMENT ,  `first_name` VARCHAR(50) NOT NULL ,  `last_name` INT(50) NOT NULL ,  `email_address` VARCHAR(50) NOT NULL ,  `phone_number` BIGINT(10) NOT NULL ,  `address` VARCHAR(60) NOT NULL ,  `query_reason` ENUM('request_volunteer_info','request_charity_info','regular_donation','info_pet_keeping','info_adoption') NOT NULL ,  `contact_pref` ENUM('email','telephone','post') NOT NULL ,  `query_details` VARCHAR(100) NOT NULL ,    PRIMARY KEY  (`id`)) ENGINE = InnoDB;
